function switchTab(tabName){document.querySelectorAll('.auth-tab').forEach(tab=>{if(tab.dataset.tab===tabName){tab.className='auth-tab flex-1 py-2.5 px-4 rounded-lg font-medium text-sm transition-all duration-200 bg-blue-500/20 text-blue-400 border border-blue-500/30';}else{tab.className='auth-tab flex-1 py-2.5 px-4 rounded-lg font-medium text-sm transition-all duration-200 text-gray-400 hover:text-white';}});document.querySelectorAll('.auth-form').forEach(form=>{form.classList.add('hidden');});document.getElementById(tabName+'-form').classList.remove('hidden');}
function togglePassword(inputId){const input=document.getElementById(inputId);const icon=input.nextElementSibling.querySelector('i');if(input.type==='password'){input.type='text';icon.className='fas fa-eye-slash';}else{input.type='password';icon.className='fas fa-eye';}}
function handleLogin(event){event.preventDefault();const button=event.target.querySelector('button[type="submit"]');const originalText=button.innerHTML;button.innerHTML='<i class="fas fa-spinner fa-spin mr-2"></i>Вход...';button.disabled=true;setTimeout(()=>{document.getElementById('success-message').classList.remove('hidden');setTimeout(()=>{button.innerHTML=originalText;button.disabled=false;alert('Успешный вход! Перенаправление на главную страницу...');},2000);},1500);}
function handleRegister(event){event.preventDefault();const password=document.getElementById('register-password').value;const confirmPassword=document.getElementById('confirm-password').value;if(password!==confirmPassword){alert('Пароли не совпадают!');return;}
const button=event.target.querySelector('button[type="submit"]');const originalText=button.innerHTML;button.innerHTML='<i class="fas fa-spinner fa-spin mr-2"></i>Создание аккаунта...';button.disabled=true;setTimeout(()=>{document.getElementById('success-message').classList.remove('hidden');setTimeout(()=>{button.innerHTML=originalText;button.disabled=false;alert('Аккаунт успешно создан! Перенаправление на главную страницу...');},2000);},2000);};